update.foreach:
update disease_list
update_time = NOW(), description = #{item.desc}, is_yibao = #{item.isYiBao}, other_name = #{item.otherName}, location = #{item.location}, infectivity = #{item.infectivity}, population = #{item.population}, symptom = #{item.symptom}, concurrent_disease = #{item.concurrentDisease}, department = #{item.department}, cost = #{item.cost}, treatment_rate = #{item.treatmentRate}, treatment_cycle = #{item.treatmentCycle}, therapeutic_method = #{item.therapeuticMethod}, correlation_check = #{item.correlationCheck}, drugs = #{item.drugs}, best_treatment_time = #{item.bestTreatmentTime}, visit_time = #{item.visitTime}, referral_frequency = #{item.referralFrequency}, preparement = #{item.preparement}
where id = #{item.id}

mapper.select:
select dl.*, dm.name as typeName from disease_list as dl left join disease_menu as dm on dl.type = dm.id
dl.status != 0
and dl.type = #{type}
and dl.id > #{lastId}
and (dl.name like concat('%',#{search},'%') or dl.other_name like concat('%',#{search},'%'))
limit
#{offset},
#{limit}

select count(*)
from disease_list as dl
left join disease_menu as dm on dl.type = dm.id

    dl.status != 0

        and dl.type = #{type}


        and dl.id > #{lastId}
